    public function saveUserLdap($userdlpa, $userId = null) {
        $retorno = false;
        $userFields = new \App\UserFields();
        $getByIdentifier = $userFields->getByIdentifier($userdlpa->getAuthIdentifier());
        
        
        if ($getByIdentifier->count() == 0) {
        

            if ($userId) {
                $userFields->department_number = $userdlpa->getDepartmentNumber();
            }
            $userFields->department_number = $userdlpa->getDepartmentNumber();
            $userFields->identifier = $userdlpa->getAuthIdentifier();

            if ($userdlpa->getTitle()) {

                $model = new \App\Title();
                $getTitleByName=$model->getByName($userdlpa->getTitle());
                if ($getTitleByName->count() == 0) {
                    $model->name = $userdlpa->getTitle();
                    $model->save();
                    $userFields->title = $model->id;
                }else{
                    $title=\App\Title::find($getTitleByName[0]->id);
                    $userFields->title = $title->id;
                }
                 
            }

            if ($userdlpa->getCountry()) {
                $model = new \App\Country();
                $getCountryByName=$model->getByName($userdlpa->getCountry());
                if ($getCountryByName->count() == 0) {
                    $model->name = $userdlpa->getCountry();
                    $model->save();
                    $userFields->country = $model->id;
                }else{
                    $country=\App\Country::find($getCountryByName[0]->id);
                    $userFields->country = $country->id;
                }
            }

            if ($userdlpa->getCompany()) {
                $model = new \App\Company();
                $getCompanyByName=$model->getByName($userdlpa->getCompany());
                if ($getCompanyByName->count() == 0) {
                    $model->name = $userdlpa->getCompany();
                    $model->save();
                    $userFields->company = $model->id;
                }else{
                    $company=\App\Company::find($getCompanyByName[0]->id);
                    $userFields->company = $company->id;
                }
            }


            if ($userdlpa->getPhysicalDeliveryOfficeName()) {
                $model = new \App\DeliveryOffice();
                $getDeliveryByName=$model->getByName($userdlpa->getPhysicalDeliveryOfficeName());
                if ($getDeliveryByName->count() == 0) {
                    $model->name = $userdlpa->getPhysicalDeliveryOfficeName();
                    $model->save();
                    $userFields->delivery_office_name = $model->id;
                }else{
                    $psicaldelivery=\App\DeliveryOffice::find($getDeliveryByName[0]->id);
                    $userFields->delivery_office_name = $psicaldelivery->id;
                }
            }

            if ($userdlpa->getDepartment()) {
                $model = new \App\Department();
                $getDepartmentByName=$model->getByName($userdlpa->getDepartment());
                if ($getDepartmentByName->count() == 0) {
                    $model->name = $userdlpa->getDepartment();
                    $model->save();
                    $userFields->department = $model->id;
                }else{
                    $department=\App\Department::find($getDepartmentByName[0]->id);
                    $userFields->department = $department->id;
                }
            }
            if($userdlpa->getEmail()!=null){
                $userFields->email = $userdlpa->getEmail();
            }else if($userdlpa->getEmail()!=null){
                $userFields->email = $userdlpa->getOtherMailbox();
            }
            $userFields->first_name = $userdlpa->getFirstName();
            $userFields->last_name = $userdlpa->getLastName();
            $userFields->info = $userdlpa->getInfo();
            $userFields->initials = $userdlpa->getInitials();
            $userFields->street_address = $userdlpa->getStreetAddress();
            $userFields->postal_code = $userdlpa->getPostalCode();
            $userFields->telephone_number = $userdlpa->getTelephoneNumber();
            $userFields->locale = $userdlpa->getLocale();
            if ($userdlpa->getThumbnailEncoded()) {
                $nameimage = preg_replace('/\s+/', '_', $userdlpa->getFirstName() . "_" . $userdlpa->getLastName() . "_" . $userdlpa->getAuthIdentifier());
                $this->save_base64_image($userdlpa->getThumbnailEncoded(), $nameimage, public_path() . "/user/fullImage/");
                $userFields->thumbnail = $nameimage. ".jpg";
                Image::make(public_path() . "/user/fullImage/" . $nameimage . ".jpg", array(
                    'width' => 300,
                    'height' => 300,
                    'Crop' => true
                ))->save(public_path() . "/user/thumbnail/" . $nameimage . ".jpg");
            }
            $retorno = $userFields->save();
             
        } else {
            if ($userId) {
                $userFields = UserFields::find($getByIdentifier[0]->id);
                $userFields->user_id = $userId;
                $userFields->save();
            }
        }
        return $retorno;
    }

